#!/bin/bash\n\n# Define the input file name\nfile="result.txt"\n\n# Initialize variables\ndeclare -A sum_api1\ndeclare -A sum_api2\n\n# Read the file line by line\nwhile IFS= read -r line; do\n    if [[ $line == "for"*"threads:" ]]; then\n        # Extract the number of threads\n        threads=${line%% *}\n    elif [[ $line == *"API1"* ]]; then\n        # Extract the API1 time and add to sum_api1 for this thread count\n        value=$(echo $line | awk '{print $7}')\n        sum_api1[$threads]=$((${sum_api1[$threads]:-0} + ${value%.*}))\n    elif [[ $line == *"API2"* ]]; then\n        # Extract the API2 time and add to sum_api2 for this thread count\n        value=$(echo $line | awk '{print $7}')\n        sum_api2[$threads]=$((${sum_api2[$threads]:-0} + ${value%.*}))\n    fi\ndone < "$file"\n\n# Print the results\nfor t in "${!sum_api1[@]}"; do\n    echo "For $t threads:"\n    echo "Sum of API1 times: ${sum_api1[$t]}"\n    if [[ -n ${sum_api2[$t]} ]]; then\n        echo "Sum of API2 times: ${sum_api2[$t]}"\n    fi\n    echo "Total sum (API1 + API2): $((sum_api1[$t] + sum_api2[$t]:-0))"\ndone