#!/bin/bash\n\nLOCAL_DIR="/path/to/local/directory"\nREMOTE_USER="your_username"\nREMOTE_HOST="remote_host_or_ip"\nREMOTE_DIR="/path/to/remote/directory"\n\n# Watch for changes in the LOCAL_DIR and synchronize them with the remote directory.\ninotifywait -m -r --format '%w%f' --event modify,create,delete,move $LOCAL_DIR \\n| while read FILE\ndo\n    echo "File changed: $FILE. Starting sync..."\n    rsync -avz --delete "$LOCAL_DIR/" "$REMOTE_USER@$REMOTE_HOST:$REMOTE_DIR/"\ndone