#!/bin/bash\n\nsource_dir="C:/images"\ntarget_dir="D:/media"\n\n# Create the target directory if it doesn't exist\nmkdir -p "$target_dir"\n\n# Find all .jpg files and move them while preserving the directory structure\nfind "$source_dir" -type f -name "*.jpg" | while read file; do\n  # Get the relative path from source to the file\n  relative_path="${file#$source_dir/}"\n  \n  # Create the directory structure in the target location if it doesn't exist\n  mkdir -p "$(dirname "$target_dir/$relative_path")"\n  \n  # Move the file preserving the directory structure\n  install --remove-source-files "$file" "$target_dir/$relative_path"\ndone