#!/bin/bash\n   echo "Connecting to VPN..."\n   sudo plog 3\n   sudo pon myvpn\n   sleep 20  # Give it time to connect\n\n   VPN_IP=$(ip addr show dev ppp0 | grep -oP '(?<=inet\s)\d+(\.\d+){3}')\n   if [ -n "$VPN_IP" ]; then\n       echo "VPN connected with IP: $VPN_IP"\n       sudo ip route add 192.168.XX.0/24 dev ppp0 src $VPN_IP metric 5\n   else\n       echo "Failed to connect"\n   fi