#!/bin/bash\n\n# Input file\ninput="originalfile.txt"\n# Output file\noutput="ascii_file.txt"\n\n# Replace special characters with ASCII equivalents\nsed -e 's/…/.../g' \          # Convert ellipsis to three dots\n    -e 's/‘/\x27/g' \        # Convert left single quote to apostrophe\n    -e 's/’/\x27/g' \        # Convert right single quote to apostrophe\n    -e 's/“/"/g' \           # Convert left double quote to double quote\n    -e 's/”/"/g' \           # Convert right double quote to double quote\n    -e 's/–/-/g' \           # Convert en dash to hyphen\n    -e 's/—/--/g'            # Convert em dash to double hyphen\n    "$input" > temp.txt\n\n# Convert encoding to 7-bit ASCII, ignoring errors\niconv -f UTF-8 -t ASCII//TRANSLIT < temp.txt > "$output"\n\n# Clean up temporary file\nrm temp.txt