#!/bin/bash\n\n# Configuration\nSOURCE_DB="source_db"\nTARGET_DB="target_db"\nUSER="your_username"\nPASSWORD="your_password"\nHOST="localhost"  # Or your target host IP address\nQUERY="SELECT * FROM your_table WHERE updated_at > '2023-10-01'"\n\n# File to store export data\nTEMP_FILE="/tmp/db_sync.sql"\n\n# Export new or changed data from the source database\nmysqldump -u $USER -p$PASSWORD $SOURCE_DB --where="$QUERY" > $TEMP_FILE\n\n# Import data into the target database\nmysql -u $USER -p$PASSWORD $TARGET_DB < $TEMP_FILE\n\n# Optionally, cleanup exported sql file\nrm $TEMP_FILE