#!/bin/bash\n\n   INOTIFYWAIT="/usr/bin/inotifywait"\n   PHP_BIN="/usr/bin/php"\n   WATCH_DIR="/path/to/recordings"\n\n   $INOTIFYWAIT -m --format '%w%f' -e create "$WATCH_DIR" | while read NEWFILE; do\n       if [[ "$NEWFILE" == *.jpg ]]; then\n           $PHP_BIN /path/to/newimage.php?location="$NEWFILE"\n           echo "Triggered PHP script for new file: $NEWFILE"\n       fi\n   done