#!/bin/bash\n\n# Files to compare\nfile1="file1.txt"\nfile2="file2.txt"\n\n# Window size\nwindow_size=10\n\n# Get line counts of both files\nlines1=$(wc -l < "$file1")\nlines2=$(wc -l < "$file2")\n\nmax_lines=$(( lines1 > lines2 ? lines1 : lines2 ))\n\nfor ((i = 1; i <= max_lines; i += window_size)); do\n    # Extract the current window from each file\n    start_line=$i\n    end_line=$((start_line + window_size - 1))\n\n    temp1="temp_file1.txt"\n    temp2="temp_file2.txt"\n\n    sed -n "${start_line},${end_line}p" "$file1" > "$temp1"\n    sed -n "${start_line},${end_line}p" "$file2" > "$temp2"\n\n    # Compare the two windows\n    echo "Comparing lines ${start_line}-${end_line}:"\n    diff -u "$temp1" "$temp2"\n    \n    # Clean up temporary files\n    rm "$temp1" "$temp2"\n    echo "----------------------------------------"\ndone