cat <<EOF | sudo tee /etc/systemd/system/gitea.service\n[Unit]\nDescription=Gitea (Git with a cup of tea)\nAfter=network.target\n\n[Service]\n# Modify these two values and both paths - if you have put gitea in a //custom// location.\nWorkingDirectory=/var/lib/gitea/\nExecStart=/usr/local/bin/gitea web --config /etc/gitea/app.ini\n\nRestartSec=2s\nType=simple\n\nUser=gitea\nGroup=gitea\n#LimitNPROC=1000\n#LimitMEMLOCK=500M\n#PrivateTmp=true\nProtectSystem=full\nReadOnlyDirectories=/ -/var/lib/gitea/ -/etc/gitea/\nReadWriteDirectories=/var/lib/gitea/{custom,data,indexers,logs}\nExecStartPre=/bin/mkdir -p /var/run/gitea\nExecStartPre=/bin/chown -R gitea:gitea /var/run/gitea\nExecStartPre=/bin/chmod -R 750 /var/run/gitea\nRestart=on-failure\n\n[Install]\nWantedBy=multi-user.target\nEOF