#!/bin/bash\n\nLOGFILE="/var/log/cpu_load.log"\n\n# Create an associative array to hold hourly sums of load and counts\ndeclare -A hourly_sum\ndeclare -A hourly_count\n\n# Read through the log file line by line\nwhile IFS= read -r line; do\n    # Extract date, time, and 1 minute load average from each line\n    datetime=$(echo "$line" | cut -d' ' -f1-2)\n    hour=${datetime%:*}\n    loadavg=$(echo "$line" | awk '{print $1}')\n    \n    # Update the sum and count for this hour\n    hourly_sum["$hour"]=$(echo "${hourly_sum[$hour]:-0} + $loadavg" | bc)\n    hourly_count["$hour"]=$(( ${hourly_count[$hour]} + 1 ))\ndone < "$LOGFILE"\n\n# Calculate and display average load for each hour\nfor hour in "${!hourly_sum[@]}"; do\n    sum=${hourly_sum[$hour]}\n    count=${hourly_count[$hour]}\n    avg=$(echo "scale=2; $sum / $count" | bc)\n    echo "$hour: Average Load = $avg"\ndone | sort