#!/bin/bash\n\n# Plan how many tests to run\necho "1..3"\n\n# Test 1: Check if the echo command works\nif [ "$(echo 'test')" = "test" ]; then\n    echo "ok 1 - Echo command"\nelse\n    echo "not ok 1 - Echo command"\nfi\n\n# Test 2: Check if a file exists\ntouch testfile.txt\nif [ -f testfile.txt ]; then\n    echo "ok 2 - File creation"\nelse\n    echo "not ok 2 - File creation"\nfi\n\n# Test 3: Clean up and check the file has been removed\nrm testfile.txt\nif ! [ -f testfile.txt ]; then\n    echo "ok 3 - File deletion"\nelse\n    echo "not ok 3 - File deletion"\nfi