#!/bin/bash\n# Filename: backup_mysql.sh\n\nDATE=$(date +%F)\nBACKUP_FILE="backup_$DATE.sql"\nDB_USER="your_db_user"\nDB_PASS="your_db_password"\nDB_NAME="your_database_name"\n\n# Create a mysqldump of the database, output to an SQL file\nmysqldump -u $DB_USER -p$DB_PASS $DB_NAME > /path/to/remote/storage/$BACKUP_FILE\n\n# Use scp to transfer the backup to your local machine\nscp /path/to/remote/storage/$BACKUP_FILE user@your_local_machine:/path/to/local/storage/\n\n# Optionally, remove the remote copy if you no longer need it\nrm /path/to/remote/storage/$BACKUP_FILE