#!/bin/bash\n\n# Directories\nWORKING_DIR="/path/to/working_dir"\nGLOBAL_REPO_PATH="http://globalServer/svn/repo/path/"\nDEV_REPO_PATH="http://devServer/svn/repo/path/"\nSYNC_USER="sync_user"\n\n# Ensure the working copy is updated with latest from dev repo\nsvn update $WORKING_DIR\n\n# Check out or overwrite global repository changes to a temp directory\nTEMP_DIR=$(mktemp -d)\nsvn checkout --force --username $SYNC_USER --password $PASSWORD $GLOBAL_REPO_PATH $TEMP_DIR\n\n# Sync the contents of the temp directory with your working copy\nperl /path/to/svn_load_dirs.pl --drop-all-empty-dirs -m "Syncing with global repo" --bypass-prop-checks $DEV_REPO_PATH/subfolder/ $TEMP_DIR\n\n# Remove temporary directory after sync is done\nrm -rf $TEMP_DIR\n\n# Commit changes to dev repository\ncd $WORKING_DIR && svn commit -m "Automated sync of external changes from global SVN"\n\n# Log the synchronization\necho "$(date): Synced global repo with dev repo" >> /path/to/sync.log