#!/bin/bash\n\nDB_USER="your_username"\nDB_PASSWORD="your_password"\nDATABASE="your_database"\n\n# List tables in the database\nTABLES=$(mysql -u $DB_USER -p$DB_PASSWORD -N -e "SHOW TABLES" $DATABASE)\n\nfor TABLE in $TABLES; do\n    echo "Exporting table: $TABLE to CSV..."\n    mysql -u $DB_USER -p$DB_PASSWORD -D $DATABASE -e "SELECT * INTO OUTFILE '/path/to/backup/$TABLE.csv' FIELDS TERMINATED BY ',' ENCLOSED BY '\"' LINES TERMINATED BY '\n' FROM $TABLE;"\ndone