#!/bin/bash\n\n# Check if the correct number of arguments is passed\nif [ "$#" ne 2 ]; then\n    echo "Usage: $0 <search_string> <replace_string>"\n    exit 1\nfi\n\n# Escape special characters in the search string for sed\nescaped_search=$(echo "$1" | sed 's/[]\/$*.^|[]/\\&/g')\n\n# Use find and sed to replace recursively\nfind . -type f -exec sed -i.bak "s|$escaped_search|$2|g" {} +