#!/bin/bash\n\nvalidate_hostname() {\n    local hostname="$1"\n    \n    # Check if the hostname is between 2 and 63 characters long,\n    # does not start or end with a hyphen,\n    # and contains only alphanumeric characters and hyphens.\n    if [[ "$hostname" =~ ^[a-zA-Z0-9]([a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?$ ]]; then\n        echo "Hostname is valid."\n    else\n        echo "Hostname is invalid. It must be 2-63 characters long, not start or end with a hyphen, and contain only alphanumeric characters and hyphens."\n    fi\n}\n\n# Example usage\nhostname="example-host"\nvalidate_hostname "$hostname"