#!/bin/bash\n\n# URL to check\nURL="http://example.com/api"\n\n# Function to send an alert email (or replace with any other notification method)\nsend_alert() {\n    echo "Web service at $URL is down!" | mail -s "Service Alert" your-email@example.com\n}\n\n# Perform the HTTP checks\nresponse=$(curl --write-out '%{http_code}' --silent --output /dev/null "$URL")\n\nif [ "$response" != 200 ] && [ "$response" != 301 ] && [ "$response" != 302 ]; then\n    send_alert\nfi