#!/bin/bash\n\n# Generate the file from dynamic content\ncat /myspecialdir/foo > /httpd/foo.txt\n\n# Output necessary headers\nprintf "Cache-control: no-cache, no-store, must-revalidate\n"\nprintf "Pragma: no-cache\n"\nprintf "Expires: 0\n"\nprintf "Location: /foo.txt\n"\nprintf "Content-type: text/html\n\n"\n\n# Optionally, you can output a minimal HTML to avoid any content issues\necho "<meta http-equiv=\"cache-control\" content=\"no-cache, no-store, must-revalidate\">"\necho "<meta http-equiv=\"pragma\" content=\"no-cache\">"\necho "<meta http-equiv=\"expires\" content=\"0\">"