# Example script to test minimal includes\n   for file in src/*.cpp; do\n       echo "Testing $file"\n       rm -f "$file".tmp\n       cp "$file" "$file".tmp\n       while grep -E '#include\s*"[^"]+\.h"' "$file"; do\n           copy="$file".backup\n           cat "$file" | grep -vF "$(grep -oE '#include\s*"[^"]+\.h"' "$file" | head -n 1)" > "$copy"\n           if g++ -c "$copy" 2>/dev/null; then\n               mv "$copy" "$file"\n           else\n               rm -f "$copy"\n               break\n           fi\n       done\n       rm -f "$file".tmp\n   done