#!/bin/bash\n\n# Ensure inotify-tools are installed\nif ! command -v inotifywait &> /dev/null; then\n    echo "inotifywait could not be found, please install inotify-tools."\n    exit 1\nfi\n\n# Create a temporary file for storing the list of created files\ncreated_files=$(mktemp)\n\n# Run inotifywait to monitor file creations in the current directory\ninotifywait -qrm --event create --format '%w%f' . > "$created_files" &\n\n# Store the PID of inotifywait for later stopping\ninotify_pid=$!\n\n# Execute your test\nrun_the_test\n\n# Stop inotifywait by killing the process\nkill $inotify_pid\n\n# Show the list of created files\ncat "$created_files"\n\n# Clean up the temporary file\nrm -f "$created_files"