# Use semantic versioning\nMAJOR=0\nMINOR=9\nPATCH=0\n\n# Increment PATCH for bugfixes, MINOR for features, MAJOR for breaking changes\nif [ -n "$NEW_PATCH" ]; then\n    PATCH=$((PATCH + 1))\nelif [ -n "$NEW_MINOR" ]; then\n    MINOR=$((MINOR + 1))\n    PATCH=0\nelif [ -n "$NEW_MAJOR" ]; then\n    MAJOR=$((MAJOR + 1))\n    MINOR=0\n    PATCH=0\nfi\n\n# Construct version number\nVERSION="$MAJOR.$MINOR.$PATCH"\necho "New version: $VERSION"