@echo off\nsetlocal\n\nREM Replace "YourServiceName" with the name of your service.\nset SERVICE_NAME=YourServiceName\n\npowershell -Command ^\n    "$service = Get-Service '%SERVICE_NAME%' -ErrorAction SilentlyContinue; " ^\n    "if ($null -eq $service) { exit 1 } else if ($service.Status -ne 'Running') { exit 2 } else { exit 0 }"\n\nif %errorlevel% equ 0 (\n    echo Service is running.\n) else if %errorlevel% equ 1 (\n    echo Service is not installed.\n) else (\n    echo Service is installed but not running.\n)\n\nendlocal