#!/bin/bash\n\n# Variables\nBACKUP_DIR="/path/to/backup"\nDATE=$(date +"%Y%m%d%H%M")\nDB_USER="your_db_user"\nDB_PASSWORD="your_db_password"\nDATABASE_NAME="your_database_name"\n\n# Create dump and compress it using gzip\nmysqldump -u $DB_USER -p$DB_PASSWORD $DATABASE_NAME | gzip > $BACKUP_DIR/backup-$DATE.sql.gz\n\n# Log the backup process (optional)\necho "Backup completed on $(date)" >> $BACKUP_DIR/backup-log.txt\n\n# Remove backups older than 7 days\nfind $BACKUP_DIR/* -mtime +7 -exec rm {} \;