#!/bin/bash\n\n# Directory containing the website files\nDIR="path/to/website"\n\n# Change into the directory\ncd "$DIR" || exit\n\n# Iterate over all references, ignoring case\ngrep -rniH --include=\*.html --include=\*.css --include=\*.js '[a-zA-Z0-9_/-]\.html' | while read -r file; do\n    # Extract the path to the referenced file from the match\n    reference=$(echo "$file" | grep -oP '(?<=:).+$')\n    \n    # Check if the referenced file exists with any case and capture its actual name\n    result=$(find . -type f -iname "$(basename "$reference")")\n\n    # If a single exact match is found, it means the cases are different but names are similar\n    if [[ $(echo "$result" | wc -l) -eq 1 ]]; then\n        echo "Mismatch found in $file: Correcting '$reference' to '$(basename "$result")'"\n        \n        # Optional: Fix the mismatch (this assumes you want references to match the actual filename case)\n        sed -i "s|$reference|$(basename "$result")|g" "$(dirname "$file")/$(grep -oP '(?<=^[^:]+:).+' <<< "$file")"\n    fi\ndone