#!/bin/bash\n\n# Define variables\nJENKINS_HOME="/var/lib/jenkins"  # Change this to your Jenkins home\nBACKUP_DIR="/path/to/backup/directory"\nDATE=$(date +"%Y%m%d_%H%M%S")\nLOG_FILE="$BACKUP_DIR/jenkins_backup.log"\n\n# Create backup directory if it doesn't exist\nmkdir -p "$BACKUP_DIR"\n\n# Backup Jenkins configuration and build history\ntar czf $BACKUP_DIR/jenkins_backup_$DATE.tar.gz -C $JENKINS_HOME jobs/ users/ config.xml userContent/\n\n# Log the result of the operation\nif [ $? -eq 0 ]; then\n    echo "$(date): Backup successful" >> $LOG_FILE\nelse\n    echo "$(date): Backup failed" >> $LOG_FILE\nfi\n\n# Optionally, rotate backups (keep for example last 7 days)\nfind $BACKUP_DIR -type f -name "*.gz" -mtime +7 -exec rm -f {} \;