#!/bin/bash\n\n# Set Oracle environment variables if needed\nexport ORACLE_SID=your_sid\nexport ORAENV_ASK=NO\n. oraenv\n\n# Check if the database is available\nsqlplus -S / as sysdba <<EOF >output.log 2>&1\nSET HEADING OFF\nSELECT name FROM v$database;\nEXIT;\nEOF\n\nif grep -q "ORA-" output.log; then\n    echo "Error: No Oracle Database detected or connection failed."\nelse\n    echo "Oracle Database is installed and running."\nfi