#!/bin/bash\n\n# Define your variables\nGATEWAY_FILE_PATH="/path/to/gateway/uploads"\nMEDIA_SERVER_USER="user"\nMEDIA_SERVER_IP="192.168.1.100"\nMEDIA_SERVER_PATH="/path/to/media/storage"\n\n# Transfer all files in the gateway directory to media server\nfor FILE in $GATEWAY_FILE_PATH/*; do\n    scp "$FILE" "${MEDIA_SERVER_USER}@${MEDIA_SERVER_IP}:${MEDIA_SERVER_PATH}/"\ndone\n\n# Optional: clean up after transfer or move to an archive folder