@echo off\nsetlocal\n\n:: Use PowerShell to get the current date in yyyy-MM-dd format\nfor /f "tokens=2 delims==" %%I in ('"powershell $d = Get-Date; '{0:yyyy-MM-dd}' -f $d"') do @set today=%%I\n\n:: Define your source and destination folders/files\nset "sourceFile=C:\path\to\your\source.log"\nset "destinationFolder=C:\path\to\archive\"\n\n:: Copy the file with the formatted date as part of its name\nxcopy "%sourceFile%" "%destinationFolder%\%today%.log" /Y\n\nendlocal