@echo off\nsetlocal EnableDelayedExpansion\n\nrem Set the directory to start from\nset "startDir=C:\proyect1"\n\nrem Set the folder name to delete\nset "folderName=_svn"\n\nrem Loop through all directories recursively and delete those matching the folderName\nfor /r "%startDir%" %%D in (%folderName%) do (\n    if exist "%%D" (\n        echo Deleting directory: %%D\n        rmdir /s /q "%%D"\n    )\n)\n\nrem Optional: Similarly, to delete _vti_* folders\nset "otherFolderPattern=_vti_*"\n\nfor /r "%startDir%" %%D in (%otherFolderPattern%) do (\n    if exist "%%D" (\n        echo Deleting directory: %%D\n        rmdir /s /q "%%D"\n    )\n)\n\nendlocal