@echo off\nsetlocal\n\nREM Set your SourceSafe database path and credentials\nset VSS_PATH="V:\Project"\nset USERNAME=YourUsername\nset PASSWORD=YourPassword\nset VIEW_LABEL=YourLabelName\n\nREM Command to list all items in the source tree\nss Dir %VSS_PATH% -R -Y%USERNAME%,%PASSWORD% > itemList.txt\n\nfor /F "tokens=* delims=" %%A in (itemList.txt) do (\n    ss Labels %%A -VL=%VIEW_LABEL% -Y%USERNAME%,%PASSWORD% | findstr "%VIEW_LABEL%" >nul\n    if %errorlevel%==0 (\n        echo Labelled File: %%A\n    )\n)\n\ndel itemList.txt\n\nendlocal