@echo off\nsetlocal enabledelayedexpansion\n\nrem Define template and placeholder content\nset "template=menu.tmpl"\nfor /f "delims=" %%i in (%template%) do set "menu_content=%%i"\n\nrem Process each HTML file in the directory that contains <!-- insert menu --> marker\nfor %%f in (*.html) do (\n    echo Processing %%f\n    for /f "tokens=1,* delims=:" %%a in ('findstr /n "^" "%%f"') do (\n        set "line=%%b"\n        if "!line!"=="<!-- insert menu -->" (\n            echo !menu_content! >> "%%f.tmp"\n        ) else (\n            echo !line! >> "%%f.tmp"\n        )\n    )\n    move /y "%%f.tmp" "%%f"\n)\n\necho All files processed.\npause