@echo off\n\nsetlocal\n\n:: Configuration\nset "server=YourServerName"\nset "database=YourDatabaseName"\nset "output_folder=C:\Path\To\OutputFolder"\n\n:: Define procedures in an array (list of procedure names)\nset procedures[0]=Procedure1\nset procedures[1]=Procedure2\nset /A count=2\n\n:: Loop through each procedure and export\nfor /L %%I in (0,1,%count%-1) do (\n    set "procedure_name=!procedures[%%I]!"\n    sqlcmd -S %server% -d %database% -E -Q "USE [%database%]; SET NOCOUNT ON; PRINT 'USE [%database%;'; EXEC sp_helptext '!procedure_name!'" > "%output_folder%\!procedure_name!.sql"\n)\n\necho Export completed.