@echo off\nsetlocal enabledelayedexpansion\n\nrem Record current date/time\necho %date%, %time% >> log.txt\necho Current date/time is %date%, %time%.\n\nrem Find the last line in log.txt and display it\nset "lastLine="\nfor /f "delims=" %%i in (log.txt) do set "lastLine=%%i"\nif defined lastLine (\n    echo Last recorded date/time: !lastLine!\n) else (\n    echo No entries found in log.txt.\n)\n\n@endlocal\n@pause\nexit