@echo off\nSETLOCAL\n\n:: Define the log name and backup location\nset LOG_NAME=System\nset BACKUP_PATH=C:\EventLogsBackup\%LOG_NAME%_%DATE:~0,2%%DATE:~3,2%%DATE:~6,4%.evtx\n\n:: Ensure the backup directory exists\nif not exist "%BACKUP_PATH%\.." mkdir "%BACKUP_PATH%\.."\n\n:: Backup the event log\nwevtutil epl %LOG_NAME% %BACKUP_PATH%\n\necho Event Log %LOG_NAME% backed up to %BACKUP_PATH%\nENDLOCAL