@echo off\nsetlocal enabledelayedexpansion\n\nrem Set the path to your MSTEST executable\nset "MSTEST_PATH=C:\Program Files (x86)\Microsoft Visual Studio 9.0\Common7\IDE\MSTest.exe"\n\nrem Set the directory where your test assemblies are located\nset "TEST_DIRECTORY=.\path_to_your_test_dlls"\n\nfor /R "%TEST_DIRECTORY%" %%f in (*.Tests.dll) do (\n    echo Running tests from: %%f\n    "%MSTEST_PATH%" /testcontainer:"%%f"\n)