@echo off\nsetlocal enabledelayedexpansion\n\n:: Set the path to your backup directory\nset "backup_dir=C:\path\to\your\backups"\n\n:: Get the current date in yyyymmdd format minus 3 days\nfor /f "tokens=2 delims==" %%I in ('wmic os get localdatetime /value') do set datetime=%%I\nset day=!datetime:~6,2!\nset month=!datetime:~4,2!\nset year=!datetime:~0,4!\npowershell -command "[datetime]::ParseExact('!year!!month!!day!', 'yyyyMMdd', $null).AddDays(-3).ToString('yyyyMMdd')" > tempdate.txt\nset /p threedaysago=<tempdate.txt\ndel tempdate.txt\n\n:: Find and delete files older than 3 days\nforfiles /P "%backup_dir%" /S /M *.bak /D -%threedaysago% /C "cmd /c del @path"