@echo off\nreg query "HKLM\SOFTWARE\Microsoft\InetStp" /v SetupString >nul 2>&1\nif %errorlevel%==0 (\n    echo IIS is installed.\n) else (\n    reg query "HKLM\SOFTWARE\Microsoft\Windows NT\CurrentVersion\ProductName" | findstr /i "Ultimate Enterprise Business"\n    if %errorlevel%==0 (\n        echo IIS can be installed on this edition.\n    ) else (\n        echo This edition of Windows does not support installation of IIS.\n    )\n)