@echo off\n   set HOST='your.sftp.server.com'\n   set USER='your_username'\n\n   (\n       echo cd /remote/directory\n       echo lcd C:\local\directory\n       echo get -a *\n       echo rm *\n   ) > sftpcommands.txt\n\n   :: Execute the SFTP command with private key authentication\n   sftp.exe -oIdentityFile=C:\path\to\your\private_key %USER%@%HOST% < sftpcommands.txt\n\n   del sftpcommands.txt