@echo off\n   setlocal\n\n   :: Define log name and backup path\n   set LOGNAME=Application\n   set BACKUPPATH=C:\Logs\Backup\%LOGNAME%\n   set TIMESTAMP=%DATE:~0,4%%DATE:~5,2%%DATE:~8,2%\n\n   :: Ensure the backup directory exists\n   if not exist "%BACKUPPATH%" mkdir "%BACKUPPATH%"\n\n   :: Export the Application log to an XML file with a datestamp in its name\n   powershell -Command "wevtutil epl %LOGNAME% %BACKUPPATH%\ApplicationLog_%TIMESTAMP%.evtx"\n\n   echo Log backup completed.\n   endlocal