@echo off\n\nsetlocal\n\nrem Set the name of the process you want to check\nset "process_name=firefox.exe"\n\nrem Check if the process is running\ntasklist /fi "imagename eq %process_name%" 2>nul | findstr /i "%process_name%" >nul\n\nif "%errorlevel%"=="0" (\n    echo Process %process_name% is running.\n    rem Add your actions here if the process is running\n) else (\n    echo Process %process_name% is not running.\n    rem Add your actions here if the process is not running\n)\n\nendlocal