@echo off\nsetlocal enabledelayedexpansion\n\nrem Change Q: below to your USB drive letter\nset "drive=Q:"\ncd /d %drive%\nfor /f "tokens=* delims=" %%i in ('dir /a:l /b/s ^| findstr "::"') do (\n    for /f "tokens=2*" %%j in ("%%i") do (\n        set "target=%%k"\n        set "symlink=%%j"\n        echo Creating junction: !symlink! pointing to !target!\n        mklink /D "!symlink!" "!target!"\n    )\n)\nendlocal