#!/bin/bash\nBACKUP_DIR="/var/backups/svn"\nTIMESTAMP=$(date "+%F")\nREPO_PATH="/var/svnrepos"\n\nmkdir -p "$BACKUP_DIR/$TIMESTAMP"\nrsync -a --delete "$REPO_PATH/" "$BACKUP_DIR/$TIMESTAMP/"\n\n# Optionally, compress backups:\ntar cfz "/tmp/$(basename $BACKUP_DIR)_$TIMESTAMP.tar.gz" -C /tmp $(basename $BACKUP_DIR)/$TIMESTAMP\nmv "/tmp/$(basename $BACKUP_DIR)_$TIMESTAMP.tar.gz" "$BACKUP_DIR/"