#!/bin/bash\n\n# Define the header and footer files\nHEADER="Header.htm"\nFOOTER="Footer.htm"\n\n# Define the directory containing content pages\nCONTENT_DIR="Parts_that_Change"\nOUTPUT_DIR="Site_Build"\n\n# Create output directory if it doesn't exist\nmkdir -p "$OUTPUT_DIR"\n\n# Loop through each HTML file in the CONTENT_DIR\nfor FILE in "$CONTENT_DIR"/*.htm; do\n    # Extract filename only (without path)\n    BASENAME=$(basename "$FILE")\n    \n    # Concatenate header, content, and footer into a new static page\n    cat "$HEADER" "$FILE" "$FOOTER" > "$OUTPUT_DIR/$BASENAME"\ndone\n\necho "Static pages have been generated in $OUTPUT_DIR."