#!/bin/bash\n\n# Directory where all your SVN repositories are located\nREPOS_DIR="/path/to/your/svn/repositories"\n\n# Backup destination directory\nBACKUP_DIR="/path/to/your/backup/location"\n\n# Ensure the backup directory exists\nmkdir -p "$BACKUP_DIR"\n\n# Iterate over each repository in the REPOS_DIR\nfor REPO_NAME in "$REPOS_DIR"/*; do\n    if [ -d "${REPO_NAME}" ]; then\n        # Get just the name of the repository (without path)\n        BASENAME=$(basename "${REPO_NAME}")\n        # Create a dump file for this repository in the backup directory\n        svnadmin dump "${REPO_NAME}" > "${BACKUP_DIR}/${BASENAME}.dump"\n        echo "Backed up ${REPO_NAME} to ${BACKUP_DIR}/${BASENAME}.dump"\n    fi\ndone\n\necho "Backup completed."