#!/bin/bash\n\n# Directory containing the zip files\nZIP_DIR="files"\n\n# Loop over all .zip files in the ZIP_DIR\nfor ZIP_FILE in "$ZIP_DIR"/*.zip; do\n    # Get the base name of the zip file (without path and extension)\n    BASENAME=$(basename "$ZIP_FILE" .zip)\n    \n    # Make a directory with the name of the zip file (without .zip)\n    mkdir -p "$ZIP_DIR/$BASENAME"\n    \n    # Unzip only image files into the created directory\n    unzip -q "$ZIP_FILE" "*.jpg" "*.png" "*.gif" -d "$ZIP_DIR/$BASENAME"\ndone\n\necho "Extraction complete."