#!/bin/bash\n\n# Eclipse installation directory\nECLIPSE_DIR="$HOME/eclipse"\n\n# Backup directory (within home)\nBACKUP_DIR="$HOME/eclipse_backup"\n\n# Check if Eclipse directory exists\nif [ -d "$ECLIPSE_DIR" ]; then\n    # Create backup directory if it doesn't exist\n    mkdir -p "$BACKUP_DIR"\n    \n    # Move current Eclipse to the backup directory, renaming with a timestamp\n    TIMESTAMP=$(date +"%Y%m%d-%H%M%S")\n    mv "$ECLIPSE_DIR" "${BACKUP_DIR}/eclipse_backup_${TIMESTAMP}"\n    \n    echo "Backup of old Eclipse completed: ${BACKUP_DIR}/eclipse_backup_${TIMESTAMP}"\nelse\n    echo "Eclipse directory not found at $ECLIPSE_DIR"\nfi\n\n# User needs to download and install the new version manually or via Eclipse Installer\necho "Please proceed with downloading and installing the new version of Eclipse."