#!/bin/bash\n\n# Path to the USB device (e.g., /dev/sdb)\nDEVICE="/dev/sdb"\n# Sector number to write the identifier to (sector size is usually 512 bytes)\nSECTOR_NUMBER=0\n# Unique identifier, e.g., 'MYUSBID1'\nUNIQUE_ID="MYUSBID1"\n\n# Write unique ID to sector\necho -n "$UNIQUE_ID" | dd of=$DEVICE bs=512 seek=$SECTOR_NUMBER count=1 conv=notrunc\n\n# Read back the unique ID from the sector\nhexdump -c -s $(($SECTOR_NUMBER * 512)) -n ${#UNIQUE_ID} $DEVICE