#!/bin/bash\n\n# Check if at least two files are provided\nif [ "$#" -lt 2 ]; then\n    echo "Usage: $0 file1 file2 [file3 ...]"\n    exit 1\nfi\n\n# Create an initial sorted list of unique lines from the first file\nsort "$1" | uniq > common.tmp\n\n# Loop through all other files and find common lines\nfor file in "${@:2}"; do\n    sort "$file" | uniq > current.tmp\n    comm -12 common.tmp current.tmp > temp.tmp\n    mv temp.tmp common.tmp\ndone\n\n# Output the common lines found in all files\ncat common.tmp\n\n# Clean up temporary files\nrm common.tmp current.tmp temp.tmp