@echo off\nsetlocal enabledelayedexpansion\n\nset "configPath=C:\path\to\web.config"\nset "searchText=oldValue"\nset "replaceText=newValue"\n\n(for /f "delims=" %%x in ('type "%configPath%" ^& break ^> "%configPath%.new" ') do (\n    set "line=%%x"\n    set "line=!line:%searchText%=%replaceText%!"\n    >>"%configPath%.new" echo(!line!\n))\n\nmove /y "%configPath%.new" "%configPath%"