# Automatically start or reattach to a screen session upon SSH login\n\n# Check if the current shell is interactive and not already in a screen session\nif [[ $- == *i* ]] && [ -z "$STY" ]; then\n    # Find any detached sessions\n    SESSION=$(screen -ls | grep Detached | awk '{print $1}' | head -n 1)\n\n    if [ -n "$SESSION" ]; then\n        # Reattach to the first found detached session\n        screen -r "$SESSION"\n    else\n        # Create a new session if none are detached\n        screen -aS mysession\n    fi\n\n    # Exit this shell as the user is now attached/inside the screen session.\n    exit 0\nfi